mapper.update:
UPDATE stories
SET story_image = #{storyImage}
WHERE story_id = #{storyId}

mapper.select:
SELECT story_id, user_id, story_image, created_at, user_name, user_avatar
FROM active_stories_view
WHERE user_id = #{userId}

